  let(:valid_attributes) {
    attributes_for(:time_table, :valid)
  }

  let(:invalid_attributes) {
    attributes_for(:time_table, :invalid)
  }

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # TimeTablesController. Be sure to keep this updated too.
  let(:valid_session) { {} }

  describe "GET #index" do
    it "returns a success response" do
      time_table = TimeTable.create! valid_attributes
      get :index, params: {}, session: valid_session
      expect(response).to be_success
    end
  end

  describe "GET #show" do
    it "returns a success response" do
      time_table = TimeTable.create! valid_attributes
      get :show, params: {id: time_table.to_param}, session: valid_session
      expect(response).to be_success
    end
  end

  describe "GET #new" do
    it "returns a success response" do
      get :new, params: {}, session: valid_session
      expect(response).to be_success
    end
  end

  describe "GET #edit" do
    it "returns a success response" do
      time_table = TimeTable.create! valid_attributes
      get :edit, params: {id: time_table.to_param}, session: valid_session
      expect(response).to be_success
    end
  end

  describe "POST #create" do
    context "with valid params" do
      it "creates a new TimeTable" do
        expect {
          post :create, params: {time_table: valid_attributes}, session: valid_session
        }.to change(TimeTable, :count).by(1)
