  describe "PUT #update" do
    context "with valid params" do
      let(:new_attributes) {
        attributes_for(:time_table, :valid_update)
      }

      it "updates the requested time_table" do
        time_table = TimeTable.create! valid_attributes
        a_time = time_table.arrival_time
        d_time = time_table.departure_time
        put :update, params: {id: time_table.to_param, time_table: new_attributes}, session: valid_session
